snippets/angular.cson.angular-directive.body:
angular.module("directive", () => {
    return {
        scope: {
            message: '='
        },
        template: '<div></div>',
        controller: function () {
            this.message = 'Thomas directive';
        },
        controllerAs: '$ctrl',
        bindToController: true
    };
});

snippets/angular.cson.angular-component.body:
angular.module("component", {
        bindings: {
            message: '='
        },
        template: '<div></div>',
        controller: function () {
            this.message = 'Thomas directive';
        }
});

snippets/angular.cson.angular-filter.body:
angular.module("filter", () => {
    return (input, option1) => {
        return input + option1;
    };
});
